import axios from "axios";
import { NOTES_API_URL } from '../constants/api'

// get notes method
export const getNotes = async () => {
    try {
        const response = await axios.get(NOTES_API_URL);

        return response.data.listOfNote;
    } catch (err) {
        console.error(err);
    }
};

export const creaNotes = async () => {
    try {
        const response = await axios.get(NOTES_API_URL);

        return response.data.listOfNote;
    } catch (err) {
        console.error(err);
    }
};
