  //       'http://localhost:5000/notes'
  //     );

  //     setNotesList(response.data.notes);
  //     console.log(notesList);
  //   } catch (err) {
  //     console.error(err);
  //   }
  // }

  return (
    <div className="App">
      <div>Notes Application</div>
      <div className="notes-list">
        {
          notesList.map((noteItem, index) => {
            return (
              <div>
                <h4>{noteItem?.text}</h4>
                <h5>{noteItem?.link}</h5>
              </div>
            )
          })
        }
      </div>
    </div>
  );
}

export default App;
